import React, { useEffect, useState } from 'react';
import logo from './logo.svg';
import './App.css';

function App() {

  const[status, setStatus] = useState("Conectando...")

  useEffect(() => {
  console.log("Tentando conectar")
  fetch("http://localhost:5152/api/test")
    .then((res) => {
      if (res.ok) {
        setStatus(`Status HTTP: ${res.status}`);
      } else {
        setStatus("Falhou ao conectar com a API");        
      }
    })
    .catch((err) => {
      setStatus("Erro ao conectar " + err.message);
    });

  // teste de fetch para rota inválida
  fetch("http://localhost:3000/api/naoexiste")
    .then(res => {
      console.log("Status /api/naoexiste:", res.status); // provavelmente 200
      return res.text();
    })
    .then(body => {
      console.log("Conteúdo /api/naoexiste:", body.slice(0, 100)); // só os primeiros 100 chars
    });
}, []);




  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Status da conexão: {status}
        </p>
      </header>
    </div>
  );
}

export default App;
